css:
/* carrinho */
.minicart__empty .lead {

  font-size: 0;

}

.minicart__empty .lead:after {

  content: 'Sacola vazia ...';

  display: block;

  font-size: calc(1.25788rem + .0945vw);

  margin-bottom: 16px;

}

.minicart__aside .card-header {

  font-size: 0;

}

/* botoes */
.header {
  background: #000 !important;
}
.header__toggler-icon {
  color: #fff !important;
}
#cart-button .fa-shopping-bag::before, #user-button .fa-user::before {
  background-color: #fff !important;
}
#cart-count {
  background: transparent
}
/* Comprar */
.product__kit .quantity-selector__buy button {
background-color: #1B8726;
border-color: #1B8726;
} /* caixinha/borda anuncios */
.product-card section {
  border: 1px solid #007dc5;
  padding: 10px
}
/* Alinhamento menu horizontal */
#s-acessorios-para-carro, #s-acessorios-para-celular,
#s-casa--automacao, #s-informatica_, #s-fones-de-ouvidos_, #s-smart-band_{
grid-template-columns: repeat(auto-fit,minmax(50%,1fr)) !important;
}
/* Alinhamento Barra de Informações */
.info-bar{
max-width: none;
}
/* Correios */ 
.shipping-calculator__option[data-label="éFácil"]:after {
content: "EXPRESSO AÉREO - PRAZO PARA PAGAMENTOS CONFIRMADOS ATÉ 11H SEG, QUA E SEX - COLETAS"
}
.shipping-calculator__option[data-label="LATAM CARGO"]:after {
content: "EXPRESSO AÉREO - PRAZO PARA PAGAMENTOS CONFIRMADOS ATÉ 11H SEG, QUA E SEX - COLETAS"
}
.shipping-calculator__option[data-label="SEDEX-HOJE ¹"]:after {
content: "Pagamento confirmado até 11h (Seg á Sex), após envio próximo dia útil - Entrega somente de Seg á Sex"
}
.shipping-calculator__option[data-label="SEDEX-12 ³"]:after {
content: "Pagamento confirmado até 12h (Seg á Sex), após envio próximo dia útil - Entrega Dia seguinte até o Meio-Dia após postagem"
}
.shipping-calculator__option[data-label="SEDEX-10 ²"]:after {
content: "Pagamento confirmado até 12h (Seg á Sex), após envio próximo dia útil - Entrega Dia seguinte até o Meio-Dia após postagem"
}
.shipping-calculator__option[data-label="SEDEX"]:after {
content: "Pagamento confirmado até 13h (Seg á Sex), após envio próximo dia útil - Entrega de Seg á Sáb sem horário periodo ou horário determinado."
} 
/* Botão de cancelar no pedido  */
.checkout__notes {
display: none !important;
}
/* Botão de cancelar no pedido */
.order-info__toggle {
    display: none !important;
}
/* Destaque pagamento PIX */
.prices__value:after {
content: "No Pix";
font-size: 17px;
}
